mapper.update:
UPDATE post
SET
title = #{ title },
content = #{ content },
updated_at = #{ updatedAt }
WHERE post_id = #{ postId }
AND author_id = #{ authorId }
AND password = #{ password }

mapper.delete:
DELETE FROM post
WHERE post_id = #{ postId }
AND author_id = #{ authorId }
AND password = #{ password }

mapper.update:
UPDATE comment
SET
content = #{content},
ip_hash = #{ipHash},
updated_at = #{updatedAt}
WHERE comment_id = #{commentId}
AND author_id = #{authorId}
AND password = #{password}

mapper.delete:
DELETE FROM comment
WHERE comment_id = #{commentId}
AND author_id = #{authorId}
AND password = #{password}
